import * as monaco from 'monaco-editor';

type IEditorOverrideServices = monaco.editor.IEditorOverrideServices

interface IRawLanguageExtensionPoint {
    id: string;
    extensions: string[];
    filenames: string[];
    filenamePatterns: string[];
    firstLine: string;
    aliases: string[];
    mimetypes: string[];
    configuration: string;
    icon: {
        light: string;
        dark: string;
    };
}

declare function getServiceOverride(): IEditorOverrideServices;

export { IRawLanguageExtensionPoint, getServiceOverride as default };
