import * as monaco from 'monaco-editor';

declare const VS_LIGHT_THEME = "vs";
declare const VS_DARK_THEME = "vs-dark";
declare const VS_HC_THEME = "hc-black";
declare const VS_HC_LIGHT_THEME = "hc-light";
interface IThemeExtensionPoint {
    id: string;
    label?: string;
    description?: string;
    path: string;
    uiTheme?: typeof VS_LIGHT_THEME | typeof VS_DARK_THEME | typeof VS_HC_THEME | typeof VS_HC_LIGHT_THEME;
    _watch: boolean;
}

type IEditorOverrideServices = monaco.editor.IEditorOverrideServices

type PartialIThemeExtensionPoint = Partial<IThemeExtensionPoint> & Pick<IThemeExtensionPoint, 'id' | 'path'>;
declare function getServiceOverride(): IEditorOverrideServices;

export { PartialIThemeExtensionPoint as IThemeExtensionPoint, getServiceOverride as default };
